export const ADD_AVATAR = `avatar/ADD_AVATAR`;
export const ADD_AVATAR_BEGIN = `avatar/ADD_AVATAR_BEGIN`;

import {bufferToBlob} from '@ciscospark/react-component-utils';

function addAvatar(userId, avatar) {
  return {
    type: ADD_AVATAR,
    payload: {
      userId,
      avatar
    }
  };
}

function addAvatarBegin(userId) {
  return {
    type: ADD_AVATAR_BEGIN,
    payload: {
      userId
    }
  };
}

/**
 * Fetches a group of users' avatars
 * @param {Array} userIds
 * @param {object} spark
 * @returns {Thunk}
 */
export function fetchAvatarsForUsers(userIds, spark) {
  const promises = [];
  return (dispatch) => {
    userIds.forEach((userId) => {
      promises.push(dispatch(fetchAvatar({userId}, spark)));
    });
    return Promise.all(promises);
  };
}

/**
 * Fetches an avatar for a given space or user id
 * @param {Object} params
 * @param {Object} params.space
 * @param {String} params.userId
 * @param {Object} spark
 * @returns {Thunk}
 */
export function fetchAvatar({space, userId}, spark) {
  return (dispatch, getState) => {
    const state = getState().avatar;
    let avatarId;
    if (space) {
      avatarId = space.id;
    }
    else {
      avatarId = userId;
    }
    const fetched = state.hasIn([`items`, avatarId]);
    const fetching = state.hasIn([`avatarsInFlight`, avatarId]);
    if (fetched || fetching) {
      return Promise.resolve();
    }

    dispatch(addAvatarBegin(avatarId));

    if (space) {
      return fetchSpaceAvatar(space, spark, userId, dispatch);
    }
    return fetchUserAvatar(userId, spark, dispatch);
  };
}

function fetchUserAvatar(userId, spark, dispatch) {
  return spark.people.get(userId)
    .then((person) => dispatch(addAvatar(userId, person.avatar)))
    .catch(() => dispatch(addAvatar(userId, undefined)));
}

function fetchSpaceAvatar(space, spark, userIdForAvatar, dispatch) {
  if (space.type === `direct`) {
    if (!userIdForAvatar) {
      return Promise.reject(new Error(`Direct spaces require a user id to display`));
    }
    return fetchUserAvatar(userIdForAvatar, spark, dispatch);
  }
  if (space.avatar) {
    return spark.internal.conversation.download(space.avatar.files.items[0])
      .then((file) => {
        const {objectUrl} = bufferToBlob(file);
        dispatch(addAvatar(space.id, objectUrl));
      });
  }
  dispatch(addAvatar(space.id, undefined));
  return Promise.resolve();
}
