
export const UPDATE_MEDIA_STATE = `media/UPDATE_MEDIA_STATE`;
export function updateMediaState(call) {
  return {
    type: UPDATE_MEDIA_STATE,
    payload: {
      call
    }
  };
}

export const UPDATE_CALL_STATE = `media/UPDATE_CALL_STATE`;
