import React from 'react';
import PropTypes from 'prop-types';
import ActivityMenu from '@ciscospark/react-component-activity-menu';
import {ICON_TYPE_MESSAGE_OUTLINE, ICON_TYPE_VIDEO_OUTLINE} from '@ciscospark/react-component-icon';

import styles from './styles.css';

export default function MessageMeetActivityMenu({
  onExit,
  onMeet,
  onMessage
}) {

  const buttons = [
    {
      label: `Message`,
      buttonClassName: styles.messageButton,
      iconType: ICON_TYPE_MESSAGE_OUTLINE,
      onClick: onMessage
    },
    {
      label: `Call`,
      buttonClassName: styles.meetButton,
      iconType: ICON_TYPE_VIDEO_OUTLINE,
      onClick: onMeet
    }
  ];
  return (
    <div className={styles.activityMenuContainer}>
      <ActivityMenu
        buttons={buttons}
        onExit={onExit}
      />
    </div>
  );
}

MessageMeetActivityMenu.propTypes = {
  onExit: PropTypes.func,
  onMeet: PropTypes.func,
  onMessage: PropTypes.func
};
